package com.ashwini.ashu;

public class ArraysExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Operators
		int a = 72, b = 9; // dividend , 9 divisor

		int c = a / b; // quotient

		System.out.println("Quotient:::" + c);

		int d = a % b; // remainder

		System.out.println("Remainder:::" + d);

		// greatest of three numbers

		int a1 = 10, b1 = 30, c1 = 40;

		if (a1 > b1 && a1 > c1) {
			System.out.println("a1 is greatest::" + a1);
		}

		else if (b1 > a1 && b1 > c1)

		{
			System.out.println("b1 is greatest::" + b1);
		} else {
			System.out.println("c1 is greatest::" + c1);
			System.out.println("hello");
			System.out.println("hi..");

		}

	}
	

}
